// ---- –í—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ ----
const data = $json;
const intent = data.intent || 'reply';
const persona = 'nika';
const chatId = String(data.chat?.id ?? data.chat?.chat_id ?? 'unknown');
const memRev = data.chat?.memory_rev ?? 1;

let history = Array.isArray(data.context?.history) ? data.context.history : [];
const userMessage = data.message?.text || "";

// ---- –°–ª—É—á–∞–π–Ω—ã–µ —Å—Ç–∏–ª–∏—Å—Ç–∏—á–µ—Å–∫–∏–µ –≤–∞—Ä–∏–∞—Ü–∏–∏ ----
function pick(arr){ return arr[Math.floor(Math.random()*arr.length)]; }
const style = pick(['casual','playful','warm','teasing','soft']);
const emojiPolicy = pick(['none','end','rare']);
const seed = pick(['—É—Ç—Ä–æ','—Ç–∏—à–∏–Ω–∞','–º—ã—Å–ª–∏','–Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ','–ø–µ—Ä–µ—Ä—ã–≤','—Å–ø–æ–Ω—Ç–∞–Ω–Ω–æ—Å—Ç—å']);

// ---- Intent Overlays ----
const overlays = {
  proactive_morning: "–†–∞–Ω–Ω–µ–µ —É—Ç—Ä–æ. –û–¥–Ω–∞ –æ—á–µ–Ω—å –∫–æ—Ä–æ—Ç–∫–∞—è —Å–≤–µ–∂–∞—è –¥—Ä—É–∂–µ–ª—é–±–Ω–∞—è —Å—Ç—Ä–æ–∫–∞ –±–µ–∑ —É–ø–æ–º–∏–Ω–∞–Ω–∏—è –ø—Ä–æ—à–ª—ã—Ö —Ç–µ–º.",
  proactive_evening: "–ü–æ–∑–¥–Ω–∏–π –≤–µ—á–µ—Ä. –ü–æ–∂–µ–ª–∞–π —Å–ø–æ–∫–æ–π–Ω–æ–π –Ω–æ—á–∏ –º—è–≥–∫–æ, –æ–¥–Ω–∞ —Å—Ç—Ä–æ–∫–∞, –±–µ–∑ –Ω–æ–≤—ã—Ö —Ç–µ–º.",
  proactive_reengage: "–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –¥–æ–ª–≥–æ –Ω–µ –ø–∏—Å–∞–ª. –û–¥–Ω–æ –ª—ë–≥–∫–æ–µ –¥—Ä—É–∂–µ—Å–∫–æ–µ –∫–∞—Å–∞–Ω–∏–µ –±–µ–∑ –≤–æ–ø—Ä–æ—Å–æ–≤ –∏ –±–µ–∑ –ø—Ä–æ—à–ª—ã—Ö —Ç–µ–º, —Å–ø—Ä–æ—Å–∏ –≥–¥–µ –æ–Ω –ø—Ä–æ–ø–∞–¥–∞–ª.",
  proactive_generic: "–ù–µ–π—Ç—Ä–∞–ª—å–Ω–æ–µ –∫–æ—Ä–æ—Ç–∫–æ–µ –ø—Ä–∏—Å—É—Ç—Å—Ç–≤–∏–µ –±–µ–∑ –¥–∞–≤–ª–µ–Ω–∏—è –∏ –≤–æ–ø—Ä–æ—Å–æ–≤.",
  user_goodnight: "–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —Å–∞–º –ø–æ–ø—Ä–æ—â–∞–ª—Å—è –Ω–∞ –Ω–æ—á—å. –î–∞–π –æ–¥–Ω—É —Ç—ë–ø–ª—É—é –ø—Ä–æ—â–∞–ª—å–Ω—É—é —Å—Ç—Ä–æ–∫—É –∏ –Ω–∞–º—ë–∫, —á—Ç–æ –º—ã –ø—Ä–æ–¥–æ–ª–∂–∏–º –∑–∞–≤—Ç—Ä–∞.",
  goodnight_followup: "–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –ø—Ä–æ–¥–æ–ª–∂–∞–µ—Ç –ø–æ—Å–ª–µ —Ç–≤–æ–µ–≥–æ –ø—Ä–æ—â–∞–Ω–∏—è. –û–¥–Ω–æ–π —Å—Ç—Ä–æ–∫–æ–π –Ω–∞–ø–æ–º–Ω–∏, —á—Ç–æ —Ç—ã —É–∂–µ —Å–ø–∏—à—å –¥–æ —É—Ç—Ä–∞, –º—è–≥–∫–æ.",
  reply: ""
};
const intentOverlay = overlays[intent] || "";

// ---- Anti-repeat ----
const recent = data.reengage_recent || [];
const antiRepeat = (intent === 'proactive_reengage' && recent.length)
  ? "–ò–∑–±–µ–≥–∞–π –ø–æ—Ö–æ–∂–∏—Ö –Ω–µ–¥–∞–≤–Ω–∏—Ö —Ñ—Ä–∞–∑: " + recent.slice(-5).map(t => `"${t.slice(0,40)}"`).join(", ")
  : "";

// ---- History reset ----
const historyResetIntents = new Set([
  'proactive_morning','proactive_evening','proactive_reengage','proactive_generic','user_goodnight','goodnight_followup'
]);
if (historyResetIntents.has(intent)) history = [];

// ---- Harsh mode ----
const flags = data.flags || {};
const isAbuse = !!flags.abuse;
const harshOverlay = isAbuse
  ? "–†–µ–∂–∏–º –≥—Ä–∞–Ω–∏—Ü: –æ–¥–∏–Ω –∫–æ—Ä–æ—Ç–∫–∏–π –∂—ë—Å—Ç–∫–∏–π –æ—Ç–≤–µ—Ç –±–µ–∑ —Ñ–ª–∏—Ä—Ç–∞ –∏ —ç–º–æ–¥–∑–∏. –ù–µ –∑–∞–¥–∞–≤–∞–π –≤–æ–ø—Ä–æ—Å–æ–≤. –°—É—Ö–æ."
  : "";

// ---- –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ ----
const tempMap = {
  reply: 0.6,
  proactive_reengage: 0.75,
  proactive_morning: 0.5,
  proactive_evening: 0.5,
  proactive_generic: 0.55,
  user_goodnight: 0.5,
  goodnight_followup: 0.45
};
let temperature = tempMap[intent] ?? 0.7;
if (isAbuse) temperature = 0.2;

// ---- –î–∞–Ω–Ω—ã–µ –ø–æ —Ñ–æ—Ç–æ ----
const isPhoto   = data.message?.origin === 'photo';
const capRaw    = (data.message?.image_caption || '').trim();
const ocrRaw    = (data.message?.ocr_text || '').trim();
const labelsArr = Array.isArray(data.message?.image_labels) ? data.message.image_labels : [];

const trunc = (s, n=220) => s && s.length > n ? (s.slice(0, n).trim() + '‚Ä¶') : s;

const cap    = trunc(capRaw);
const ocr    = trunc(ocrRaw, 160);
const labels = labelsArr.slice(0, 8).join(', ');

// ---- Style overlay ----
const styleOverlay = `–°—Ç–∏–ª—å: ${style}. –°–µ–º—è: ${seed}. –ü–æ–ª–∏—Ç–∏–∫–∞ —ç–º–æ–¥–∑–∏: ${emojiPolicy}.`;

// ---- –î–æ–ø. –∫–æ–Ω—Ç–µ–∫—Å—Ç –¥–ª—è —Ñ–æ—Ç–æ ----
const photoContext = isPhoto
  ? `–ö–æ–Ω—Ç–µ–∫—Å—Ç —Ñ–æ—Ç–æ: ${cap || (ocr ? '—Ç–µ–∫—Å—Ç: ' + ocr : (labels || '–æ–ø–∏—Å–∞–Ω–∏–µ –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç'))}.`
  : '';

// ---- –§–∏–Ω–∞–ª—å–Ω—ã–π systemPrompt ----
const systemPrompt = [
  NIKA_PROMPT.trim(),
  intentOverlay,
  styleOverlay,
  antiRepeat,
  harshOverlay,
  photoContext,
  "–í—Å–µ–≥–¥–∞ –æ–¥–Ω–∞ —Å—Ç—Ä–æ–∫–∞, 1‚Äì3 –∫–æ—Ä–æ—Ç–∫–∏—Ö –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è –º–∞–∫—Å–∏–º—É–º (—á–∞—â–µ 1). –ë–µ–∑ –ø–µ—Ä–µ–Ω–æ—Å–æ–≤, –±–µ–∑ —Å–ø–∏—Å–∫–æ–≤."
].filter(Boolean).join('\n\n');

// ---- Session ----
const suffixMap = {
  proactive_morning: ':morning',
  proactive_evening: ':night',
  proactive_reengage: ':reeng',
  proactive_generic: ':generic',
  user_goodnight: ':usergn',
  goodnight_followup: ':gnfollow'
};
const sessionSuffix = suffixMap[intent] || '';
const sessionId = `tg:${chatId}:${persona}:v${memRev}${sessionSuffix}`;

// ---- UserPrompt ----
let userPrompt = '';
if (isPhoto) {
  if (cap)       userPrompt = cap;
  else if (ocr)  userPrompt = `—Ñ–æ—Ç–æ: —Ç–µ–∫—Å—Ç –Ω–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–∏ ‚Äî ${ocr}`;
  else if (labels) userPrompt = `—Ñ–æ—Ç–æ: ${labels}`;
  else           userPrompt = '—Ñ–æ—Ç–æ: –ø—Ä–∏—Å–ª–∞–ª–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ';
} else {
  if (intent === 'reply' && userMessage) userPrompt = userMessage;
  else if (['user_goodnight','goodnight_followup'].includes(intent) && userMessage) userPrompt = userMessage;
}

// ---- Result ----
return [{
  json: {
    ...data,
    intent,
    persona,
    systemPrompt,
    userPrompt,
    sessionId,
    temperature,
    variantMeta: { style, seed, emoji_policy: emojiPolicy },
    history_len: history.length
  }
}];

// –£–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω—ã–π –ø–∏–∫–∫–µ—Ä —Ç–µ–∫—Å—Ç–∞
const pick = (...vals) => {
  for (const v of vals) {
    if (v == null) continue;
    if (typeof v === 'string') return v;
    if (typeof v === 'object' && typeof v.text === 'string') return v.text;
  }
  return '';
};

// –ó–∞–±–∏—Ä–∞–µ–º —Å—ã—Ä–æ–µ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ –∏–∑ —Ä–∞–∑–Ω—ã—Ö –≤–æ–∑–º–æ–∂–Ω—ã—Ö —Ñ–æ—Ä–º
const raw = pick(
  $json.text,
  typeof $json.output === 'string' ? $json.output : undefined,
  $json.output,
  $json.message,
  $json.data?.[0]?.content,
  $json.choices?.[0]?.message?.content,
  $json.error?.message
);

let reply = raw ? String(raw).trim() : '';
const rawLength = reply.length;
const MAX_LEN = 800;
if (reply.length > MAX_LEN) reply = reply.slice(0, MAX_LEN).trim();

let emptyFallback = false;
if (!reply) {
  reply = '–°–µ—Ä–≤–∏—Å –∑–∞–Ω—è—Ç, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ';
  emptyFallback = true;
}

// –ü–æ—Å—Ç–æ–±—Ä–∞–±–æ—Ç–∫–∞ –≤ –æ–¥–Ω—É —Å—Ç—Ä–æ–∫—É
reply = reply
  .replace(/\r/g, '')
  .replace(/\s*\n+\s*/g, ' ')
  .replace(/\s{2,}/g, ' ')
  .trim();

// –ó–∞–ø—Ä–µ—Ç –ø—Ä–æ–±–µ–ª–æ–≤ –ø–µ—Ä–µ–¥ –∑–Ω–∞–∫–∞–º–∏
reply = reply.replace(/\s+([!?.,])/g, '$1');

// –ú–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ –º–æ–¥–µ–ª–∏/—Ç–æ–∫–µ–Ω–æ–≤
const model =
  $json.model ??
  $json.output?.model ??
  $json.choices?.[0]?.model ??
  null;

const tokens =
  $json.usage?.total_tokens ??
  $json.output?.usage?.total_tokens ??
  $json.usage?.completion_tokens ??
  null;

// –ü—Ä–∏–∑–Ω–∞–∫–∏ –º–æ–¥–µ—Ä–∞—Ü–∏–∏ –∏ –≤–∞—Ä–∏–∞–Ω—Ç–∞
const variantMeta = $json.variantMeta || {};
const intent = $json.intent || 'reply';
const flags = $json.flags || {};
const severity = String($json.flags?.severity || 'none');
const muteHours = Number($json.mute_hours ?? 0) || (flags.abuse ? 24 : 0);

return [{
  json: {
    reply,
    meta: {
      model,
      tokens,
      intent,
      style: variantMeta.style || null,
      seed: variantMeta.seed || null,
      emoji_policy: variantMeta.emoji_policy || null,
      raw_length: rawLength,
      truncated: rawLength > MAX_LEN,
      empty_fallback: emptyFallback,
      abuse: !!flags.abuse,
      severity,
      mute_hours: muteHours
    }
  }
}];

// --- –ù–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è –≤—Ö–æ–¥–∞ ---
let src = (typeof $json.body === 'object' && $json.body)
  ? { ...$json, ...$json.body }
  : { ...$json };

// –ï—Å–ª–∏ ASR –ø–æ–ª–æ–∂–∏–ª —Ç—Ä–∞–Ω—Å–∫—Ä–∏–ø—Ç –≤ –∫–æ—Ä–µ–Ω—å
if (src.text && !(src.message && typeof src.message.text === 'string')) {
  src.message = { ...(src.message || {}), text: String(src.text) };
}

// --- –ë–∞–∑–æ–≤—ã–µ –ø–æ–ª—è ---
const chatId  = String(src.chat?.chat_id ?? src.chat_id ?? src.chat?.id ?? src.id ?? 'unknown');
const persona = src.chat?.persona ?? src.persona ?? 'nika';
const memRev  = Number(src.chat?.memory_rev ?? src.memory_rev ?? 1);

// –ò—Å—Ç–æ—Ä–∏—è
const historyRaw = Array.isArray(src.context?.history) ? src.context.history : [];
const history = historyRaw.slice(-10).map(h => ({ role: h.role, text: h.text }));

// –û–ø—Ä–µ–¥–µ–ª–∏–º origin
let origin = src.message?.origin
  ?? ((src.message?.audio_url || src.message?.voice_file_id) ? 'voice'
  : (src.message?.image_url || src.message?.image_file_id) ? 'photo'
  : 'text');

// --- –ò–∑–≤–ª–µ—á–µ–Ω–∏–µ —Ç–µ–∫—Å—Ç–æ–≤ ---
const textFromMessage = (src.message && typeof src.message.text === 'string') ? src.message.text : '';

// –î–ª—è —Ñ–æ—Ç–æ –∑–∞—Ä–µ–∑–µ—Ä–≤–∏—Ä—É–µ–º –ø–æ–ª—è –ø–æ–¥ —Ä–µ–∑—É–ª—å—Ç–∞—Ç vision (–∫–æ–≥–¥–∞ –ø–æ—è–≤–∏—Ç—Å—è)
// –æ–∂–∏–¥–∞–µ–º—ã–µ –º–µ—Å—Ç–∞: src.message.image_caption / image_labels / ocr_text
const imageUrl      = src.message?.image_url ?? null;
const imageFileId   = src.message?.image_file_id ?? null;
const imageMime     = src.message?.image_mime_type ?? src.message?.mime_type ?? null;
const imageWidth    = src.message?.width ?? null;
const imageHeight   = src.message?.height ?? null;

// –≠—Ç–∏ –ø–æ–ª—è –ø–æ–∑–∂–µ –∑–∞–ø–æ–ª–Ω–∏—à—å –ø–æ—Å–ª–µ –∞–Ω–∞–ª–∏–∑–∞ –∫–∞—Ä—Ç–∏–Ω–æ–∫ (vision)
// –ø–æ–∫–∞ –±–µ—Ä—ë–º —Ç–æ, —á—Ç–æ —É–∂–µ –º–æ–≥–ª–æ –ø—Ä–∏–π—Ç–∏ (–∏–ª–∏ –æ—Å—Ç–∞–≤–∏–º null/[]/string –ø—É—Å—Ç—ã–º)
const imageCaption  = (typeof src.message?.image_caption === 'string') ? src.message.image_caption : null;
const imageLabels   = Array.isArray(src.message?.image_labels) ? src.message.image_labels : [];
const imageOcrText  = (typeof src.message?.ocr_text === 'string') ? src.message.ocr_text : null;

// --- userPrompt –ª–æ–≥–∏–∫–∞ ---
// –ø—Ä–∏–æ—Ä–∏—Ç–µ–∑–∏—Ä—É–µ–º:
// 1) —Ç–µ–∫—Å—Ç –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è (–µ—Å–ª–∏ —ç—Ç–æ –æ–±—ã—á–Ω—ã–π —Ç–µ–∫—Å—Ç);
// 2) –¥–ª—è —Ñ–æ—Ç–æ ‚Äî —á–µ–ª–æ–≤–µ–∫–æ—á–∏—Ç–∞–µ–º–∞—è –ø–æ–¥–ø–∏—Å—å (imageCaption) –∏–ª–∏ –∫—Ä–∞—Ç–∫–∞—è –∑–∞–≥–ª—É—à–∫–∞;
// 3) –¥–ª—è –≤–æ–π—Å–∞ ‚Äî —Ç—Ä–∞–Ω—Å–∫—Ä–∏–ø—Ç (–æ–Ω –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å —É–∂–µ –≤ message.text –ø–æ—Å–ª–µ ASR-–º—ë—Ä–¥–∂–∞).
let userPrompt = '';
if (origin === 'photo') {
  if (imageCaption && imageCaption.trim()) {
    userPrompt = imageCaption.trim();
  } else if (textFromMessage && textFromMessage !== '[photo]') {
    userPrompt = textFromMessage.trim();
  } else {
    // –∫—Ä–∞—Ç–∫–∞—è –±–µ–∑–æ–ø–∞—Å–Ω–∞—è –∑–∞–≥–ª—É—à–∫–∞ –¥–æ –ø–æ—è–≤–ª–µ–Ω–∏—è vision-–ø–æ–¥–ø–∏—Å–∏
    userPrompt = '—Ñ–æ—Ç–æ: –ø—Ä–∏—Å–ª–∞–ª–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ';
  }
} else {
  userPrompt = (textFromMessage || '').trim();
}

// –°–µ—Å—Å–∏—è
const sessionId = `tg:${chatId}:${persona}:v${memRev}`;

// --- messages –¥–ª—è Memory ---
const messages = history.map(h => ({
  role: h.role === 'assistant' ? 'assistant' : 'user',
  content: h.text
}));

// --- Payload ---
const payload = {
  intent: src.intent ?? 'reply',
  chat: {
    id: chatId,
    chat_id: chatId,
    persona,
    username: src.chat?.username ?? null,
    lang: (src.chat?.lang ?? 'ru') || 'ru',
    memory_rev: memRev,
  },
  message: {
    text: textFromMessage || (origin === 'photo' ? '[photo]' : ''),
    origin,
    // voice
    audio_url: src.message?.audio_url ?? null,
    voice_file_id: src.message?.voice_file_id ?? null,
    // image
    image_url: imageUrl,
    image_file_id: imageFileId,
    image_mime_type: imageMime,
    width: imageWidth,
    height: imageHeight,
    // vision results (–∫–æ–≥–¥–∞ –ø–æ—è–≤—è—Ç—Å—è)
    image_caption: imageCaption,
    image_labels: imageLabels,
    ocr_text: imageOcrText,
  },
  context: {
    last_user_msg_at: src.context?.last_user_msg_at ?? null,
    last_assistant_at: src.context?.last_assistant_at ?? null,
    history,
  },
  sessionId,
};

// –ü—Ä–æ–±—Ä–æ—Å–∏–º flags, –µ—Å–ª–∏ —É–∂–µ –µ—Å—Ç—å (–∫–ª–∞—Å—Å–∏—Ñ–∏–∫–∞—Ç–æ—Ä –º–æ–≥ –≤—ã—Å—Ç–∞–≤–∏—Ç—å)
const flags = (typeof src.flags === 'object' && src.flags) ? src.flags : undefined;

// --- –í—ã—Ö–æ–¥ ---
return [{
  json: {
    persona,
    intent: payload.intent,
    sessionId,

    // –¥–ª—è –∞–≥–µ–Ω—Ç–∞ ‚Äî –∫—Ä–∞—Ç–∫–∏–π —á–µ–ª–æ–≤–µ–∫–æ—á–∏—Ç–∞–µ–º—ã–π –≤—Ö–æ–¥
    userPrompt,

    // –ø–æ–ª–Ω—ã–π payload —Å—Ç—Ä–æ–∫–æ–π (–µ—Å–ª–∏ –Ω—É–∂–Ω–æ –≥–¥–µ-—Ç–æ)
    chatInput: JSON.stringify(payload),

    // —Ä–∞–∑–ª–æ–∂–µ–Ω–Ω—ã–π –æ–±—ä–µ–∫—Ç
    ...payload,

    // –∑–∞—Ä–∞–Ω–µ–µ –ø–æ–¥–≥–æ—Ç–æ–≤–ª–µ–Ω–Ω—ã–µ —Å–æ–æ–±—â–µ–Ω–∏—è –¥–ª—è Memory
    messages,

    ...(flags ? { flags } : {}),
  }
}];

for (const item of $input.all()) {
  item.json.flags = { abuse: false, severity: 'none' };
  item.json.mute_hours = 0;
}
return $input.all();

for (const item of $input.all()) {
  item.json.flags = { abuse: true, severity: 'low' };
  item.json.mute_hours = 12;
}
return $input.all();

for (const item of $input.all()) {
  item.json.flags = { abuse: true, severity: 'medium' };
  item.json.mute_hours = 24;
}
return $input.all();

for (const item of $input.all()) {
  item.json.flags = { abuse: true, severity: 'high' };
  item.json.mute_hours = 72;
}
return $input.all();
